package br.com.piii.controller;

import br.com.piii.model.Role;
import br.com.piii.model.User;
import br.com.piii.service.RoleService;
import br.com.piii.service.UserService;
import org.apache.tomcat.util.codec.binary.Base64;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.security.core.context.SecurityContextHolder;
import org.springframework.stereotype.Controller;
import org.springframework.validation.BindingResult;
import org.springframework.web.bind.annotation.*;
import org.springframework.web.multipart.MultipartFile;
import org.springframework.web.servlet.ModelAndView;

@Controller
@RequestMapping(value = "/user")
public class UserController {

    @Autowired
    private UserService userService;

    @Autowired
    private RoleService roleService;

    @GetMapping("")
    public ModelAndView findAll(){
        ModelAndView mv = new ModelAndView("user/list");
        mv.addObject("users", userService.findAll());
        mv.addObject("username", SecurityContextHolder.getContext().getAuthentication().getName());
        return mv;
    }

    @GetMapping("/add")
    public ModelAndView save(User user) {
        ModelAndView mv = new ModelAndView("user/add");
        mv.addObject("user", user);
        //Load Roles
        mv.addObject("profiles", roleService.findAll());
        mv.addObject("username", SecurityContextHolder.getContext().getAuthentication().getName());
        return mv;
    }

    @GetMapping("/addNew")
    public ModelAndView saveNew(User user) {
        ModelAndView mv = new ModelAndView("user/addNew");
        mv.addObject("user", user);
        //Load Roles
        mv.addObject("profiles", roleService.findAll());
        mv.addObject("username", null);
        return mv;
    }

    @PostMapping("/add")
    public ModelAndView save(@RequestParam(value = "file", required = false) MultipartFile file, User user, BindingResult result) {
        try {
            if (file == null || file.isEmpty()) {
                user.setPhoto("data:image/jpeg;base64,");
            } else {
                byte[] image = Base64.encodeBase64(file.getBytes());
                String result2 = new String(image);
                user.setPhoto("data:image/jpeg;base64," + result2);
            }
        } catch (Exception e) {
            user.setPhoto("data:image/jpeg;base64,");
            e.printStackTrace();
        }
        if (user.getRole() == null) {
            Role role = roleService.findById(0);
            user.setRole(role);
        }
        if (result.hasErrors()) {
            return save(user);
        }
        userService.save(user);
        ModelAndView mv = new ModelAndView("main/principal");
        mv.addObject("success", true);
        String userName = SecurityContextHolder.getContext().getAuthentication().getName();
        if(userName.equals("anonymousUser")){
            return mv;
        }
        return findAll();
    }

    @GetMapping("/delete/{id}")
    public ModelAndView delete(@PathVariable("id") int id){
        ModelAndView mv = new ModelAndView("user/delete");
        User user = userService.findById(id);
        mv.addObject("user", user);
        mv.addObject("username", SecurityContextHolder.getContext().getAuthentication().getName());
        return mv;
    }

    @PostMapping("/delete")
    public ModelAndView delete(User user, BindingResult result){
        if(result.hasErrors()) {
            return delete(user.getId());
        }
        userService.deleteById(user.getId());
        return findAll();
    }

    @GetMapping("/edit/{id}")
    public ModelAndView edit(@PathVariable("id") int id){
        ModelAndView mv = new ModelAndView("user/edit");
        User user = userService.findById(id);
        mv.addObject("user", user);
        mv.addObject("profiles", roleService.findAll());
        mv.addObject("username", SecurityContextHolder.getContext().getAuthentication().getName());
        return mv;
    }

    @PostMapping("/edit")
    public ModelAndView edit(@RequestParam("file") MultipartFile file, User user, BindingResult result) {
        try {
            if (file.isEmpty()) {
                user.setPhoto("data:image/jpeg;base64,");
            } else {
                byte[] image = Base64.encodeBase64(file.getBytes());
                String byteImage = new String(image);
                user.setPhoto("data:image/jpeg;base64," + byteImage);
            }
        } catch (Exception e) {
            user.setPhoto("data:image/jpeg;base64,");
            e.printStackTrace();
        }
        if (result.hasErrors()) {
            return edit(user.getId());
        }
        userService.save(user);
        return findAll();
    }
}
